// good
import defaultExport from './foo';
const FOO = 'BAR';
`,paraId:84,tocIndex:45},{value:"\u4E0D\u5141\u8BB8\u91CD\u590D\u5BFC\u51FA\u76F8\u540Cname\u7684\u53D8\u91CF\u4EE5\u53CAdefault\u3002",paraId:85,tocIndex:46},{value:'"import/export": 2',paraId:85,tocIndex:46},{value:`// bad
export const foo = function () { /*...*/ };
function bar() { /*...*/ }
export { bar as foo };
// bad
