function makeClass() { return new MyClass(...arguments) }
export default makeClass;
`,paraId:86,tocIndex:46},{value:"\u4E0D\u5141\u8BB8\u5BFC\u51FAlet\u4FEE\u9970\u7684\u53D8\u91CF\u3002",paraId:87,tocIndex:47},{value:'"import/no-mutable-exports": 2',paraId:87,tocIndex:47},{value:`// bad
