export default foo;
`,paraId:90,tocIndex:48},{value:"generator\u7684\u51FD\u6570\u7B7E\u540D\u4E2D\u7A7A\u683C\u7684\u4E66\u5199\u5E94\u4E3Afunction* () {}\u3002",paraId:91,tocIndex:50},{value:'"generator-star-spacing": [2, "after"]',paraId:91,tocIndex:50},{value:"\u4E3A\u4EC0\u4E48\uFF1Ffunction\u548C*\u662Fgenerator\u51FD\u6570\u5173\u952E\u5B57\u7684\u4E00\u90E8\u5206\uFF0C\u4E0D\u540C\u4E8Efunction\uFF0C\u5E94\u8BE5\u8FDE\u5728\u4E00\u8D77\u5199\u3002",paraId:92,tocIndex:50},{value:`// bad
function * foo() {}
function *foo() {}
function*foo() {}
