`,paraId:3,tocIndex:3},{value:"\u907F\u5F00 ",paraId:4,tocIndex:3},{value:"Date",paraId:4,tocIndex:3},{value:" \u5BF9\u8C61\u590D\u6742\u6027\uFF0C\u89E3\u6790\u5206\u949F\u6570\u66F4\u7B80\u6D01\uFF0C\u5982 ",paraId:4,tocIndex:3},{value:"const totalMinutes = hour * 60 + minute",paraId:4,tocIndex:3},{value:"\u3002",paraId:4,tocIndex:3},{value:"\u6392\u5E8F\u540E\u76F8\u90BB\u6BD4\u8F83\uFF0C\u6BD4\u8F83\u6B21\u6570\u5C11\uFF0C\u914D\u5408 ",paraId:4,tocIndex:3},{value:"Math.min",paraId:4,tocIndex:3},{value:" \u5904\u7406\u8DE8\u5929\u5DEE\u503C\u5F88\u5DE7\u5999\u3002",paraId:4,tocIndex:3},{value:"\u5904\u7406\u65F6\u95F4\u7B97\u6CD5\u8981\u7559\u610F\u7279\u6B8A\u683C\u5F0F\u4E0E\u5FAA\u73AF\u7279\u6027\uFF0C\u5C3D\u91CF\u7B80\u5316\u65F6\u95F4\u5904\u7406\uFF0C\u6392\u5E8F\u76F8\u90BB\u6BD4\u8F83\u80FD\u63D0\u6548\u3002\u82E5\u65F6\u95F4\u5217\u8868\u5141\u8BB8\u91CD\u590D\uFF0C\u9700\u5728\u5F00\u5934\u52A0\u91CD\u590D\u68C0\u6D4B\u73AF\u8282\u3002",paraId:5,tocIndex:4},{value:`/**
 * 2 <= timePoints.length <= 2 * 104
 * timePoints[i]\u683C\u5F0F\u4E3A "HH:MM"
 *
 * @param {string[]} timePoints
 * @return {number}
 */
let findMinDifference = function (timePoints) {
  // \u5224\u65AD\u662F\u4E0D\u662F\u6570\u7EC4
  if (!Array.isArray(timePoints) || timePoints.length < 2) {
    return 0;
  }
  // \u5C06\u65F6\u95F4\u5B57\u7B26\u4E32\u8F6C\u6362\u4E3A\u5206\u949F\u6570\uFF0C\u5904\u740600:xx\u683C\u5F0F
  const minutesList = [];
  for (let i = 0, len = timePoints.length; i < len; i++) {
    const [hour, minute] = timePoints[i].split(':').map(Number);
    const totalMinutes = hour * 60 + minute;
    minutesList.push(totalMinutes);
    if (hour === 0) {
      minutesList.push(totalMinutes + 1440); // \u52A0\u4E0A24\u5C0F\u65F6\u5BF9\u5E94\u7684\u5206\u949F\u6570\uFF0C\u5904\u740600:xx\u683C\u5F0F
    }
  }
  // \u5BF9\u5206\u949F\u6570\u6570\u7EC4\u8FDB\u884C\u6392\u5E8F
  minutesList.sort((a, b) => a - b);
  // \u521D\u59CB\u5316\u6700\u5C0F\u65F6\u95F4\u5DEE\u4E3A\u4E00\u4E2A\u8F83\u5927\u503C
  let minDiffTime = Infinity;
  // \u8BA1\u7B97\u76F8\u90BB\u5143\u7D20\u95F4\u7684\u5DEE\u503C\uFF0C\u540C\u65F6\u5904\u7406\u5FAA\u73AF\u7684\u60C5\u51B5\uFF08\u6BD4\u598223:59\u548C00:00\uFF09
  for (let i = 0, len = minutesList.length; i < len - 1; i++) {
    const diff = Math.min(
      minutesList[i + 1] - minutesList[i],
      1440 - (minutesList[i + 1] - minutesList[i])
    );
    minDiffTime = Math.min(minDiffTime, diff);
  }
  return minDiffTime;
};
