function preOrderTraversal(root) {
  if (root === null) {
    return;
  }

  console.log(root.value); // \u8BBF\u95EE\u6839\u8282\u70B9
  preOrderTraversal(root.left);
  preOrderTraversal(root.right);
}

preOrderTraversal(root); // \u8F93\u51FA\uFF1A1,2,4,5,3

// \u4E2D\u5E8F\u904D\u5386
function inOrderTraversal(root) {
  if (root === null) {
    return;
  }

  inOrderTraversal(root.left);
  console.log(root.value);// \u8BBF\u95EE\u6839\u8282\u70B9
  inOrderTraversal(root.right);
}
inOrderTraversal(root);// \u8F93\u51FA\uFF1A4,2,5,1,3

// \u540E\u5E8F\u904D\u5386
function postOrderTraversal(root) {
  if (root === null) {
    return;
  }

  postOrderTraversal(root.left);
  postOrderTraversal(root.right);
  console.log(root.value);// \u8BBF\u95EE\u6839\u8282\u70B9
}
postOrderTraversal(root); // 4,5,2,3,1
`,paraId:23,tocIndex:5},{value:"\u5728\u4E0A\u8FF0\u4EE3\u7801\u4E2D\uFF0C\u6211\u4EEC\u9996\u5148\u5B9A\u4E49\u4E86\u4E00\u4E2ABinarySearchTreeNode\u7C7B\u6765\u8868\u793A\u6709\u5E8F\u4E8C\u53C9\u6811\u7684\u8282\u70B9\u3002\u7136\u540E\uFF0C\u6211\u4EEC\u5B9E\u73B0\u4E86\u4E00\u4E2AinsertNode\u51FD\u6570\u6765\u63D2\u5165\u8282\u70B9\uFF0C\u5E76\u4FDD\u6301\u6709\u5E8F\u4E8C\u53C9\u6811\u7684\u6027\u8D28\u3002\u6700\u540E\uFF0C\u6211\u4EEC\u901A\u8FC7\u8C03\u7528inOrderTraversal\u51FD\u6570\u6765\u4E2D\u5E8F\u904D\u5386\u6709\u5E8F\u4E8C\u53C9\u6811\uFF0C\u5E76\u5C06\u7ED3\u679C\u8F93\u51FA\u5230\u63A7\u5236\u53F0\u3002",paraId:24,tocIndex:6},{value:`// \u5B9A\u4E49\u6709\u5E8F\u4E8C\u53C9\u6811\u8282\u70B9\u7C7B
class BinarySearchTreeNode {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}

// \u63D2\u5165\u8282\u70B9
function insertNode(root, value) {
  if (root === null) {
    root = new BinarySearchTreeNode(value);
    return root;
  }

  if (value < root.value) {
    root.left = insertNode(root.left, value);
  } else if (value > root.value) {
    root.right = insertNode(root.right, value);
  }

  return root;
}
